import NewExpense from "../NewExpense/NewExpense";
import React, { useState } from "react";

const ExpenseFormDisplay = (props) => {
  const [expenses, setExpenses] = useState(props.dummyExpenses);
  const [formDisplay, setFormDisplay] = useState(false);

  const clickHandler = () => {
    setFormDisplay(true);
  };

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => [expense, ...prevExpenses]);
    props.onSubmit(expenses);
  };

  {
    if (formDisplay === false)
      return <button onClick={clickHandler}>Add new expense</button>;
  }

  return <NewExpense onAddExpense={addExpenseHandler} />;
};

export default ExpenseFormDisplay;
